INSERT INTO `products` VALUES (1,'photocopy Ricoh Aficio MP 4001','photocopy-ricoh-aficio-mp-4001','photocopy Ricoh Aficio MP 4001 mô tả','<p>ĐIỂM NỔI BẬT CỦA SẢN PHẨM</p>\r\n\r\n<h3><strong>L&iacute; do n&ecirc;n mua&nbsp;m&aacute;y photocopy&nbsp;tại Phước&nbsp;An</strong></h3>\r\n\r\n<p>&rarr;&nbsp;M&aacute;y photocopy b&aacute;n&nbsp;l&agrave; những thiết bị ch&iacute;nh h&atilde;ng, kh&ocirc;ng sử dụng linh&nbsp;kiện rẻ tiền, kh&ocirc;ng r&otilde; nguồn gốc.</p>\r\n\r\n<p>&rarr;&nbsp;Ch&uacute;ng t&ocirc;i lu&ocirc;n đảm bảo m&aacute;y chất lượng tốt nhất cho kh&aacute;ch h&agrave;ng&nbsp;bẳng c&aacute;ch bảo tr&igrave;, gi&aacute;m định thường xuy&ecirc;n.</p>\r\n\r\n<p>&rarr;&nbsp;Hổ trợ nhanh ch&oacute;ng,&nbsp;c&ugrave;ng đội ngũ nh&acirc;n vi&ecirc;n đ&ocirc;ng đảo kỹ thuật cao, để đảm bảo đ&aacute;p ứng trong thời gian nhanh nhất.</p>\r\n\r\n<p>&rarr; Phước An c&oacute; kinh nghiệm gần 15 năm&nbsp;trong lĩnh vực&nbsp;<strong>B&aacute;n m&aacute;y Photocopy.</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &ndash; Giảm gi&aacute; mạnh</strong>&nbsp;khi mua&nbsp;m&aacute;y photocopy&nbsp;tại Phước An.</p>\r\n\r\n<p>Hỗ trợ nhanh qua số:&nbsp;<strong>08.3720.6886 &ndash; 0908.763.368</strong>.</p>\r\n\r\n<h2><strong>T&iacute;nh năng chung&nbsp;của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Hỗ trợ một loạt c&aacute;c tinh năng&nbsp;ho&agrave;n thiện v&agrave;&nbsp;chuy&ecirc;n nghiệp</li>\r\n	<li>Điều khiển bằng m&agrave;n h&igrave;nh m&agrave;u&nbsp;cảm ứng</li>\r\n	<li>Qu&eacute;t h&igrave;nh ảnh duplex 2 mặt tự động l&agrave;m giảm ti&ecirc;u thụ giấy</li>\r\n</ul>\r\n\r\n<h2><strong>T&iacute;nh năng Copier&nbsp;của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Khay nạp hỗ trợ l&ecirc;n đến h&agrave;ng ngh&igrave;n bản</li>\r\n	<li>Hỗ trợ qu&eacute;t một lần để in nhiều lần nhầm&nbsp;tối đa h&oacute;a hiệu suất đầu ra</li>\r\n	<li>Lưu trữ v&agrave; lấy t&agrave;i liệu tr&ecirc;n web với c&aacute;c m&aacute;y chủ dữ liệu</li>\r\n</ul>\r\n\r\