INSERT INTO `products` VALUES (1,'photocopy Ricoh Aficio MP 4001','photocopy-ricoh-aficio-mp-4001','photocopy Ricoh Aficio MP 4001 mô tả','<p>ĐIỂM NỔI BẬT CỦA SẢN PHẨM</p>\r\n\r\n<h3><strong>L&iacute; do n&ecirc;n mua&nbsp;m&aacute;y photocopy&nbsp;tại Hoang Lai</strong></h3>\r\n\r\n<p>&rarr;&nbsp;M&aacute;y photocopy b&aacute;n&nbsp;l&agrave; những thiết bị ch&iacute;nh h&atilde;ng, kh&ocirc;ng sử dụng linh&nbsp;kiện rẻ tiền, kh&ocirc;ng r&otilde; nguồn gốc.</p>\r\n\r\n<p>&rarr;&nbsp;Ch&uacute;ng t&ocirc;i lu&ocirc;n đảm bảo m&aacute;y chất lượng tốt nhất cho kh&aacute;ch h&agrave;ng&nbsp;bẳng c&aacute;ch bảo tr&igrave;, gi&aacute;m định thường xuy&ecirc;n.</p>\r\n\r\n<p>&rarr;&nbsp;Hổ trợ nhanh ch&oacute;ng,&nbsp;c&ugrave;ng đội ngũ nh&acirc;n vi&ecirc;n đ&ocirc;ng đảo kỹ thuật cao, để đảm bảo đ&aacute;p ứng trong thời gian nhanh nhất.</p>\r\n\r\n<p>&rarr; Hoang Lai c&oacute; kinh nghiệm gần 15 năm&nbsp;trong lĩnh vực&nbsp;<strong>B&aacute;n m&aacute;y Photocopy.</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &ndash; Giảm gi&aacute; mạnh</strong>&nbsp;khi mua&nbsp;m&aacute;y photocopy&nbsp;tại Hoang Lai.</p>\r\n\r\n<p>Hỗ trợ nhanh qua số:&nbsp;<strong>08.3720.6886 &ndash; 0908.763.368</strong>.</p>\r\n\r\n<h2><strong>T&iacute;nh năng chung&nbsp;của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Hỗ trợ một loạt c&aacute;c tinh năng&nbsp;ho&agrave;n thiện v&agrave;&nbsp;chuy&ecirc;n nghiệp</li>\r\n	<li>Điều khiển bằng m&agrave;n h&igrave;nh m&agrave;u&nbsp;cảm ứng</li>\r\n	<li>Qu&eacute;t h&igrave;nh ảnh duplex 2 mặt tự động l&agrave;m giảm ti&ecirc;u thụ giấy</li>\r\n</ul>\r\n\r\n<h2><strong>T&iacute;nh năng Copier&nbsp;của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Khay nạp hỗ trợ l&ecirc;n đến h&agrave;ng ngh&igrave;n bản</li>\r\n	<li>Hỗ trợ qu&eacute;t một lần để in nhiều lần nhầm&nbsp;tối đa h&oacute;a hiệu suất đầu ra</li>\r\n	<li>Lưu trữ v&agrave; lấy t&agrave;i liệu tr&ecirc;n web với c&aacute;c m&aacute;y chủ dữ liệu</li>\r\n</ul>\r\n\r\n<h2><strong>C&aacute;c t&iacute;nh năng&nbsp;in&nbsp;của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Chuẩn Ethernet v&agrave; cổng USB 2.0 với tốc độ cao, t&ugrave;y chọn kết nối trực tiếp hoặc&nbsp;kh&ocirc;ng d&acirc;y</li>\r\n	<li>Tốc độ cao v&agrave;i chục trang mỗi ph&uacute;t &nbsp;với độ ph&acirc;n giải tối thiểu 600 dpi</li>\r\n	<li>T&ugrave;y chọn in tập tin PDF gi&uacute;p&nbsp;giảm lưu lượng mạng bằng c&aacute;ch gửi c&aacute;c tập tin PDF trực tiếp tới m&aacute;y in m&agrave; kh&ocirc;ng cần mở phần mềm Adobe Acrobat</li>\r\n</ul>\r\n\r\n<h2><strong>C&aacute;c t&iacute;nh năng Scan của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>C&agrave;i đặt t&ecirc;n tập tin tự động khi scan</li>\r\n	<li>C&aacute;c t&agrave;i liệu được scan sang c&aacute;c định dạng tập tin phổ biến nhất, bao gồm PDF, TIFF, hoặc JPEG</li>\r\n	<li>C&oacute; thể xem trước tập tin trước khi truyền đi</li>\r\n</ul>\r\n\r\n<h2><strong>T&iacute;nh năng Fax&nbsp;của Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Tốc độ Fax đạt 33,6 Kbps th&ocirc;ng qua internet</li>\r\n	<li>Hỗ trợ Fax nội bộ qua mạng Lan v&agrave; t&igrave;m kiếm LDAP</li>\r\n	<li>T&ugrave;y chọn quản l&yacute; nội dung Fax qua hộp thư đến, qua ổ ứng, hoặc 1 thư mục</li>\r\n</ul>\r\n\r\n<h2><strong>C&aacute;c t&iacute;nh năng bảo mật&nbsp;tr&ecirc;n Ricoh Aficio MP 4001</strong></h2>\r\n\r\n<ul>\r\n	<li>Ngăn chặn việc sao ch&eacute;p c&aacute;c t&agrave;i liệu nhạy cảm bằng c&aacute;ch sử dụng điều khiển sao ch&eacute;p tr&aacute;i ph&eacute;p</li>\r\n	<li>T&ugrave;y chọn ghi đ&egrave; tự động khi c&aacute;c tập tin h&igrave;nh ảnh được scan xong</li>\r\n	<li>M&atilde; h&oacute;a to&agrave;n bộ dữ liệu trong ổ cứng</li>\r\n	<li>Tập tin PDF được m&atilde; h&oacute;a tự động khi truyền</li>\r\n</ul>',18000000,'Máy','thumb/y5at0qtt3qwnFK6X8741fgBkTaFUUHpPdsSLCmW3.webp',2,1,'SELL',1,'[\"Máy photocopy Ricoh laser trắng đen\", \"Khổ giấy tối đa: A3\", \"Tốc độ (trang/phút A4): 40\", \"Dung lượng bộ nhớ